package ethereum

import (
	"bytes"
	"context"
	"log/slog"
	"testing"

	"github.com/stretchr/testify/require"
)

func TestFetchVaa(t *testing.T) {
	ctx := context.TODO()

	var logBuffer bytes.Buffer
	testHandler := slog.NewJSONHandler(&logBuffer, nil)
	logger := slog.New(testHandler)

	res, err := fetchVaa(ctx, logger, uint64(10002), uint64(45756), "0x7B1bD7a6b4E61c2a123AC6BC2cbfC614437D0470", "")
	require.NoError(t, err)

	expectedVAA := ""
	require.Equal(t, expectedVAA, res.Data.VAA)
}
